@article{10.11648/j.ss.20190802.11, author = {Natalia Gulius}, title = {Clan Culture: Challenges and Potential for University During Transformation Period (Russia)}, journal = {Social Sciences}, volume = {8}, number = {2}, pages = {36-43}, doi = {10.11648/j.ss.20190802.11}, url = {https://doi.org/10.11648/j.ss.20190802.11}, eprint = {https://article.sciencepublishinggroup.com/pdf/10.11648.j.ss.20190802.11}, abstract = {The clan corporate culture of the university as a culture of collaboration and cooperation is a possible answer to the search for unity between two opposing trends of a modern university. There is a tension between the traditional professorial university culture and innovation and market culture which helps to overcome the contemporary challenges of globalization and competition in the educational market. It is obvious that the clan culture impedes the university transformation into an economic corporation and contributes to the university community preservation and professional and personal identity. The relevance of the study lies in the fact that the university corporate culture configuration as a management tool helps the formation of a common vision of the world-class university. Right now, in a competitive situation within the Russian excellence program 5-100 it is crucial for university to ensure loyalty of employees, students, university partners and university environment. The university clan corporate culture is responsible for introducing innovative brands and for preserving the classical heritage to transforming into new type of the university.}, year = {2019} }

