@Article{Uhlenbeck:1924:JHD, author = "George E. Uhlenbeck", title = "Over {Johannes Heckius}. ({Dutch}) [{On Johannes Heckius}]", journal = "Mededeelingen van het {Nederlandsch Historisch Instituut} te {Rome}", volume = "4", pages = "217--228", year = "1924", bibdate = "Tue Apr 23 16:53:33 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", URL = "http://resources.huygens.knaw.nl/retroboeken/knir/#page=11&accessor=toc1&view=imagePane&size=686&source=4", acknowledgement = ack-nhfb, ajournal = "Commun. Dutch Hist. Inst. Rome", author-dates = "Samuel A. Goudsmit (1902--1978); George E. Uhlenbeck (1900--1988)", remark = "This is Uhlenbeck's first publication, about history, rather than physics. The subject is one of the four cofounders of the Accademia del Lincei (Academy of Lynxes) in Rome in 1603.", } @Article{Goudsmit:1925:OSV, author = "S. Goudsmit and G. E. Uhlenbeck", title = "Opmerking over de spectra van waterstof en helium. ({Dutch}) [{Note} on the spectra of hydrogen and helium]", journal = j-PHYSICA-THE-HAGUE, volume = "5", number = "??", pages = "266--270", month = "????", year = "1925", CODEN = "PYSIA7", ISSN = "0370-2707", bibdate = "Tue Apr 23 14:10:52 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/goudsmit-samuel-a.bib; https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", author-dates = "Samuel A. Goudsmit (1902--1978); George E. Uhlenbeck (1900--1988)", fjournal = "Physica (The Hague)", language = "Dutch", remark = "This is Goudsmit and Uhlenbeck's first joint paper.", xxnote = "TO DO: find it in archives", } @Article{Uhlenbeck:1925:EHU, author = "George E. Uhlenbeck and Samuel Goudsmit", title = "{Ersetzung der Hypothese vom unmechanischen Zwang durch eine Forderung bez{\"u}glich des inneren Verhaltens jedes einzelnen Elektrons}. ({German}) [{Replacement} of the hypothesis of unmechanical force with a requirement for the inner behavior of each electron]", journal = j-NATURWISSENSCHAFTEN, volume = "13", number = "47", pages = "953--954", month = nov, year = "1925", CODEN = "NATWAY", DOI = "https://doi.org/10.1007/BF01558878", ISSN = "0028-1042 (print), 1432-1904 (electronic)", ISSN-L = "0028-1042", bibdate = "Tue Jul 03 07:38:19 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/b/bohr-niels.bib; https://www.math.utah.edu/pub/bibnet/authors/g/goudsmit-samuel-a.bib; https://www.math.utah.edu/pub/bibnet/authors/p/pauli-wolfgang.bib; https://www.math.utah.edu/pub/bibnet/authors/s/slater-john-clarke.bib; https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", note = "See \cite{Lorentz:1936:U} for a computation in 1927 of the radius of a rotating charged particle with the spin of the electron.", URL = "http://link.springer.com/article/10.1007/BF01558878; http://www.springerlink.com/content/u20q85248gp8166q/", acknowledgement = ack-nhfb, author-dates = "Samuel A. Goudsmit (1902--1978); George E. Uhlenbeck (1900--1988)", fjournal = "Naturwissenschaften", journal-URL = "http://link.springer.com/journal/114", language = "German", remark-1 = "In this paper, Goudsmit and Uhlenbeck proposed the concept of electron spin, a critical step forward in the quantum mechanics of many-electron systems. Surprisingly, that work did not get them the Nobel Prize. After World War II, Goudsmit was the scientific head of the Alsos mission to capture German nuclear scientists. In the 1950s, he founded the journal Physical Review Letters. During the 1960s and 1970s, he was Editor-in-Chief of the main American physics journal, the Physical Review. Wolfgang Pauli presented his Exclusion Principle in 1925, for which he received the 1945 Nobel Prize in Physics. Pauli tightened the Exclusion Principle to include spin in \cite{Pauli:1940:CBS}.", remark-2 = "Pais \cite[page 302]{Pais:2000:GSP} says of this paper: ``\,`Lorentz has shown us that this is nonsense.' And so we said to Ehrenfest, `We'd better not publish that note.' And then Ehrenfest said `I have sent it away already weeks ago and it will appear next week!' And then he said to us --- I don't know whether Same remembers, but I remember --- `Well, Sie beide sind jung; Sie k{\"o}nnen sich eine Dummheit leisten!' [You are both young, you can afford a stupidity]", remark-3 = "Pais \cite[page 302]{Pais:2000:GSP} reports about the author order: ``The discovery was published with Uhlenbeck as first author and Goudsmit as second because (George told me) Ehrenfest suggested that this order would avoid the impression that George was only Sem's [Sem was Samuel's nickname] student, while Sem himself preferred to come second because it was George who had first thought of spin.''", remark-4 = "See also the second \cite{Uhlenbeck:1926:SES} and third \cite{Goudsmit:1926:RES} papers.", } @Article{Uhlenbeck:1925:SVL, author = "G. E. Uhlenbeck", title = "Over een stelling van {Lorentz} en haar uitbreiding voor meerdimensionale ruimten. ({Dutch}) [{On} a statement by {Lorentz} and its extension for multi-dimensional spaces]", journal = j-PHYSICA-THE-HAGUE, volume = "5", number = "??", pages = "423--428", month = "????", year = "1925", CODEN = "PYSIA7", ISSN = "0370-2707", bibdate = "Tue Apr 23 14:06:48 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", acknowledgement = ack-nhfb, author-dates = "Samuel A. Goudsmit (1902--1978); George E. Uhlenbeck (1900--1988)", fjournal = "Physica (The Hague)", language = "Dutch", xxnote = "TO DO: find it in archives", } @Article{Ehrenfest:1926:CDM, author = "P. Ehrenfest and G. E. Uhlenbeck", title = "On the connection of different methods of solution of the wave equation in multi-dimensional spaces", journal = "Verhandelingen der Koninklijke Akademie van Wetenschappen te Amsterdam. Wis- en natuurkundige vakken", volume = "29", number = "??", pages = "1280--1285", month = "????", year = "1926", bibdate = "Tue Apr 23 14:16:04 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", acknowledgement = ack-nhfb, ajournal = "Proc. Kon. Akad. Wetensch.", author-dates = "Paul Ehrenfest (1880--1933), George E. Uhlenbeck (1900--1988)", xxjournal = "Proc. Acad. Amsterdam", xxjournal = "Verhandelingen der koninklijke {Akademie van Wetenschappen te Amsterdam}", xxnote = "TO DO: Find correct full journal title. I cannot match volume with year in the Hathi Trust invisible holdings. Biodiversity Heritage Library site has only volumes 1 (1892) to 12 (1915--1919).", } @Article{Ehrenfest:1926:GVB, author = "P. Ehrenfest and G. E. Uhlenbeck", title = "{Graphische Veranschaulichung der De Broglieschen Phasenwellen in der f{\"u}nfdimensionalen Welt von O. Klein}. ({German}) [{Graphical} Illustration of {de Broglie}'s Phase Waves in the Five-Dimensional World by {O. Klein}]", journal = j-Z-PHYSIK, volume = "39", number = "7--8", pages = "495--498", month = jul, year = "1926", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/bf01322089", ISSN = "0044-3328", bibdate = "Thu Apr 18 15:17:47 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/d/debroglie-louis.bib; https://www.math.utah.edu/pub/bibnet/authors/u/uhlenbeck-george-e.bib", acknowledgement = ack-nhfb, author-dates = "Paul Ehrenfest (1880--1933), George E. Uhlenbeck (1900--1988)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", language = "German", received = "16 September 1926", } @Article{Goudsmit:1926:KQI, author = "S. Goudsmit and G. E. Uhlenbeck", title = "{Die Kopplungsm{\"o}glichkeiten der Quantenvektoren im Atom}. ({German}) [{The} coupling possibilities of quantum vectors in the atom]", journal = j-Z-PHYSIK, volume = "35", number = "8--9", pages = "618--625", month = aug, year = "1926", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01379808", ISSN = "0044-3328", ISSN-L = "0044-3328", bibdate = "Wed Apr 17 05:59:47 MDT 2019", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/goudsmit-samuel-a.bi