@article{10.11648/j.earth.s.2015040501.11, author = {Nodar Elizbarashvili and Giorgi Meladze and Maia Meladze and David Svanadze and Lizi Gadrani and Josef Lazarashvili}, title = {Some Urgent Issues of Georgia’s Geography}, journal = {Earth Sciences}, volume = {4}, number = {5-1}, pages = {1-7}, doi = {10.11648/j.earth.s.2015040501.11}, url = {https://doi.org/10.11648/j.earth.s.2015040501.11}, eprint = {https://article.sciencepublishinggroup.com/pdf/10.11648.j.earth.s.2015040501.11}, abstract = {Geography is facing new challenges in Georgia. On the one hand, it is related to evaluation of current condition of the country and its geo-ecological issues, and on the other hand, the requirements of practice and the prospects of socio-economic development. The requirements of practice imply several urgent issues such as new orientation of the land use and the optimal use of the region's natural-resource potential, demographic stability and development of mountainous areas. The prospects of the country’s development include the urgent issues such as global environmental problems and prediction of geo-system conditions, development of military geography and planning of environmentally strained areas. In order to present and solve these problems effectively, Georgian School of Geography should play a key role, which will become a prerequisite for its further development.}, year = {2015} }

