{{Infobox country |conventional_long_name = <!--Formal or official full name of the country in English--> |native_name = <!--Country's name (usually full name) in its official/defacto language(s), hence in italics (double quotemarks)--> |common_name = <!--Common name in English (used for wikilinks, captions, and to produce a default iso3166 code)--> |status = <!--Status of country--> |image_flag = <!--e.g. Flag of country.svg--> |alt_flag = <!--alt text for flag (text shown when pointer hovers over flag)--> |flag_border = <!--set to no to disable border around the flag--> |image_flag2 = <!--e.g. Second-flag of country.svg--> |alt_flag2 = <!--alt text for second flag--> |flag2_border = <!--set to no to disable border around the flag--> |image_coat = <!--e.g. Coat of arms of country.svg--> |alt_coat = <!--alt text for coat of arms--> |symbol_type = <!--emblem, seal, etc (if not a coat of arms)--> |national_motto = <!--in inverted commas and wikilinked if link exists--> |englishmotto = <!--English language version of motto--> |national_anthem = <!--in inverted commas and wikilinked if link exists--> |royal_anthem = <!--in inverted commas and wikilinked if link exists--> |other_symbol_type = <!--Use if a further symbol exists, e.g. hymn--> |other_symbol = |image_map = <!--e.g. LocationCountry.svg--> |loctext = <!--text description of location of country--> |alt_map = <!--alt text for map--> |map_caption = <!--Caption to place below map--> |image_map2 = <!--Another map, if required--> |alt_map2 = <!--alt text for second map--> |map_caption2 = <!--Caption to place below second map--> |capital = <!--Name of country/territory's capital, wikilinked if link exists--> |coordinates = <!-- Coordinates for capital, using {{tl|coord}} --> |largest_city = <!--Name of country/territory's largest city. Use "capital" (without quotation marks) if it's the capital.--> |largest_settlement_type = <!--Type of settlement if largest settlement not a city--> |largest_settlement = <!--Name of largest settlement--> |official_languages = <!--Languages recognised in legislation, constitution, etc--> |national_languages = <!--Country/territory-wide languages recognised but not necessarily in country/territory-wide law, etc--> |regional_languages = <!--Languages recognised or associated with particular regions within the country/territory--> |languages_type = <!--Use to specify a further type of language, if not official, national or regional--> |languages = <!--Languages of the further type--> |languages_sub = <!--Is this further type of language a sub-item of the previous non-sub type? ("yes" or "no")--> |languages2_type = <!--Another further type of language--> |languages2 = <!--Languages of this second further type--> |languages2_sub = <!--Is the second alternative type of languages a sub-item of the previous non-sub type? ("yes" or "no")--> |ethnic_groups = <!--List/breakdown of ethnic groups within the country/territory--> |ethnic_groups_year = <!--Year of ethnic groups data (if provided)--> |ethnic_groups_ref = <!--(for any ref/s to associate with ethnic groups data)--> |religion = <!--Religion--> |religion_year = <!--Year of religion data (if provided)--> |religion_ref = <!--(for any ref/s to associate with religion data)--> |demonym = <!--Term/s describing those associated with the country/territory (e.g. "Belgian" for the country Belgium)--> |government_type = <!--(often a compound multi-wikilinked term, e.g. "Federal semi-presidential constitutional republic", etc)--> |leader_title1 = <!--(for a country, usually the head of state's (wikilinked) title, e.g. "President", "Monarch")--> |leader_name1 = |leader_title2 = <!--(could be "Vice President", otherwise "Prime Minster", etc, etc)--> |leader_name2 = |leader_title14 = <!--(up to 14 distinct leaders may be included)--> |leader_name14 = |legislature = <!--Name of the country/territory's governing body, e.g. "Parliament", "Congress", etc--> |upper_house = <!--Name of governing body's upper house, if given (e.g. "Senate")--> |lower_house = <!--Name of governing body's lower house, if given (e.g. "Chamber of Deputies")--> |sovereignty_type = <!--Brief description of country/territory's status ("Independence [from...]", "Autonomous province [of...]", etc)--> |sovereignty_note = |established_event1 = <!--First key event in history of country/territory's status or formation--> |established_date1 = <!--Date of first key event--> |established_event2 = <!--Second key event--> |established_date2 = <!--Date of second key event--> |established_event13 = <!--(up to 13 distinct events may be included)--> |established_date13 = |area_rank = |area = <!--Major area size (in [[Template:convert]] either km2 or sqmi first)--> |area_km2 = <!--Major area size (in square km)--> |area_sq_mi = <!--Area in square mi (requires area_km2)--> |area_footnote = <!--Optional footnote for area--> |percent_water = |area_label = <!--Label under "Area" (default is "Total")--> |area_label2 = <!--Label below area_label (optional)--> |area_data2 = <!--Text after area_label2 (optional)--> |population_estimate = |population_estimate_rank = |population_estimate_year = |population_census = |population_census_year = |population_density_km2 = |population_density_sq_mi = |population_density_rank = |nummembers = <!--An alternative to population--> |GDP_PPP = <!--(Gross Domestic Product from Purchasing Power Parity)--> |GDP_PPP_rank = |GDP_PPP_year = |GDP_PPP_per_capita = |GDP_PPP_per_capita_rank = |GDP_nominal = |GDP_nominal_rank = |GDP_nominal_year = |GDP_nominal_per_capita = |GDP_nominal_per_capita_rank = |Gini = <!--(Gini measure of income inequality; input number only; valid values are between 0 and 100)--> |Gini_ref = <!--(for any ref/s to associate with Gini number)--> |Gini_rank = |Gini_year = |HDI_year = <!-- Please use the year to which the data refers, not the publication year--> |HDI = <!--(Human Development Index; input number only; valid values are between 0 and 1)--> |HDI_change = <!--increase/decrease/steady; rank change from previous year--> |HDI_rank = |HDI_ref = <!--(for any ref/s to associate with HDI number)--> |currency = <!--Name/s of currency/ies used in country/territory--> |currency_code = <!--ISO 4217 code/s for currency/ies (each usually three capital letters)--> |time_zone = <!--e.g. GMT, PST, AST, etc, etc (wikilinked if possible)--> |utc_offset = <!--in the form "+N", where N is number of hours offset--> |time_zone_DST = <!--Link to DST (Daylight Saving Time) used, otherwise leave empty--> |utc_offset_DST = <!--in the form "+N", where N is number of hours offset--> |DST_note = <!--Optional note regarding DST use--> |antipodes = <!--Place/s exactly on the opposite side of the world to country/territory--> |date_format = <!--all-numeric date format and era, such as [[Common Era|CE]], [[Anno Domini|AD]], [[Hijri year|AH]], etc.; e.g. {{abbr|yyyy|year}}-{{abbr|mm|month}}-{{abbr|dd|day}} ([[Anno Domini|AD]]) --> |drives_on = <!--"right" or "left" (side of road)--> |cctld = <!--Internet country code top-level domain identifier (e.g. [[.br]], [[.de]], etc)--> |iso3166code = <!--ISO code only; no extra text. Use to override default from common_name parameter above; omit using "omit".--> |calling_code = <!--e.g. [[+1]], [[+531]], [[+44]], etc--> |patron_saint = <!--Use patron_saints for multiple--> |image_map3 = <!--Optional third map position, e.g. for use with reference to footnotes below it--> |alt_map3 = <!--alt text for third map position--> |footnote_a = <!--For any footnote <sup>a</sup> used above--> |footnote_b = <!--For any footnote <sup>b</sup> used above--> |footnote_h = <!--For any footnote <sup>h</sup> used above--> |footnotes = <!--For any generic non-numbered footnotes--> }}

{{Infobox former country |native_name = <!-- Name in a modern syntax of native language(s). Leave blank if name is only in English. Separate with line breaks<br/> or use Template:Plainlist. If language uses Latin characters, place name(s) in italics. --> |conventional_long_name = <!-- Full name in English --> |common_name = <!-- Name to be used in constructing links and category names; not for display --> |iso3166code = omit <!-- For a country or geopolitical version of a country that ceased to exist prior to the introduction of iso3166--> |era = <!-- Use: "Napoleonic Wars", "Cold War", etc. --> |status = <!-- Status: see Category list on template page --> |status_text = <!-- A free text to describe status at the top of the infobox. Use sparingly. --> |empire = <!-- The empire or country to which the entity was in a state of dependency --> |government_type = <!-- To generate categories: "Monarchy", "Republic", etc. to generate categories --> <!-- Rise and fall, events, years and dates --> <!-- only fill in the start/end event entry if a specific article exists. Don't just say "abolition" or "declaration" --> |event_start = <!-- Default: "Established" --> |date_start = <!-- Optional: Date of establishment, in format 1 January (no year) --> |year_start = <!-- Year of establishment --> |event_end = <!-- Default: "Disestablished" --> |date_end = <!-- Optional: Date of disestablishment, in format 1 January (no year) --> |year_end = <!-- Year of disestablishment --> |year_exile_start = <!-- Year of start of exile (if dealing with exiled government: status="Exile") --> |year_exile_end = <!-- Year of end of exile (leave blank if still in exile) --> |event1 = <!-- Optional: other events between "start" and "end" --> |date_event1 = |event2 = |date_event2 = |event3 = |date_event3 = |event4 = |date_event4 = |event5 = |date_event5 = |event6 = |date_event6 = |event_pre = <!-- Optional: A crucial event that took place before "event_start" --> |date_pre = |event_post = <!-- Optional: A crucial event that took place after "event_end" --> |date_post = <!-- Flag navigation: Preceding and succeeding entities p1 to p5 and s1 to s5 --> |p1 = <!-- Name of the article for preceding entity, numbered 1-5 --> |flag_p1 = <!-- Default: "Flag of {{{p1}}}.svg" (size 30) --> |image_p1 = <!-- Use: [[File:Sin escudo.svg|20px|Image missing]] --> |p2 = |flag_p2 = |p3 = |flag_p3 = |p4 = |flag_p4 = |p5 = |flag_p5 = |s1 = <!-- Name of the article for succeeding entity, numbered 1-5 --> |flag_s1 = <!-- Default: "Flag of {{{s1}}}.svg" (size 30) --> |image_s1 = <!-- Use: [[File:Sin escudo.svg|20px|Image missing]] --> |s2 = |flag_s2 = |s3 = |flag_s3 = |s4 = |flag_s4 = |s5 = |flag_s5 = |image_flag = <!-- Default: Flag of {{{common_name}}}.svg --> |flag_alt = <!-- Alt text for flag --> |image_flag2 = <!-- Second flag --> |flag_alt2 = <!-- Alt text for second flag --> |flag = <!-- Link target under flag image. Default: Flag of {{{common_name}}} --> |flag2 = <!-- Link target under flag2 image. Default: Flag of {{{common_name}}} --> |flag_type = <!-- Displayed text for link under flag. Default "Flag" --> |flag2_type = <!-- Displayed text for link under flag2. Default "Flag" --> |image_coat = <!-- Default: Coat of arms of {{{common_name}}}.svg --> |coa_size = <!-- Size of coat of arms --> |coat_alt = <!-- Alt text for coat of arms --> |symbol_type = <!-- Displayed text for link under symbol. Default "Coat of arms" --> |symbol_type_article = <!-- Link target under symbol image. Default: Coat of arms of {{{common_name}}} --> |image_map = |image_map_alt = |image_map_caption = |image_map2 = <!-- If second map is needed; does not appear by default --> |image_map2_alt = |image_map2_caption = |capital = |capital_exile = <!-- If status="Exile" --> |national_motto = |national_anthem = |common_languages = |religion = |demonym = |currency = <!-- Titles and names of the first and last leaders and their deputies --> |leader1 = <!-- Name of king or president --> |leader2 = |leader3 = |leader4 = |leader21 = <!--(up to 21 distinct leaders may be included)--> |year_leader1 = <!-- Years served --> |year_leader2 = |year_leader3 = |year_leader4 = |year_leader21 = |title_leader = <!-- Default: "King" for monarchy, otherwise "President" --> |representative1 = <!-- Name of representative of head of state (e.g. colonial governor) --> |representative2 = |representative3 = |representative4 = |representative5 = |year_representative1 = <!-- Years served --> |year_representative2 = |year_representative3 = |year_representative4 = |year_representative5 = |title_representative = <!-- Default: "Governor" --> |deputy1 = <!-- Name of prime minister --> |deputy2 = |deputy3 = |deputy4 = |year_deputy1 = <!-- Years served --> |year_deputy2 = |year_deputy3 = |year_deputy4 = |title_deputy = <!-- Default: "Prime minister" --> <!-- Legislature --> |legislature = <!-- Name of legislature --> |house1 = <!-- Name of first chamber --> |type_house1 = <!-- Default: "Upper house" --> |house2 = <!-- Name of second chamber --> |type_house2 = <!-- Default: "Lower house" --> <!-- Area and population of a given year --> |stat_year1 = <!-- year of the statistic, specify either area, population or both --> |stat_area1 = <!-- area in square kilometres (w/o commas or spaces); area in square miles is calculated --> |stat_pop1 = <!-- population (w/o commas or spaces) --> |stat_year2 = |stat_area2 = |stat_pop2 = |stat_year3 = |stat_area3 = |stat_pop3 = |stat_year4 = |stat_area4 = |stat_pop4 = |stat_year5 = |stat_area5 = |stat_pop5 = |today = <!-- Present-day countries that overlap with the territorial extent of the former country. Do not use this parameter if there are more than four such countries. --><!-- Do NOT add flags, per MOS:INFOBOXFLAG --> |footnote_a = <!-- Accepts wikilinks --> |footnote_b = <!-- Accepts wikilinks --> ... |footnote_h = <!-- Accepts wikilinks --> |footnotes = <!-- Accepts wikilinks --> }}

