@Article{Gamow:1926:MNS, author = "George Gamow and Lev D. Landau and Dimitri D. Ivanenko", title = "On the motion of nonconservative systems with one degree of freedom. ({Russian})", journal = j-ZH-RUSS-FIZ-KHIM-OBSHCH-CHAST-FIZ, volume = "58", number = "3A", pages = "477--482", month = "????", year = "1926", CODEN = "ZRFFAS", ISSN = "0372-9834", bibdate = "Thu May 31 07:21:26 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", acknowledgement = ack-nhfb, ajournal = "Zh. Russ. Fiz.-Khim. Obshch., Ch. Fiz.", author-dates = "George Gamow (1904--1968)", fjournal = "Zhurnal Russkogo Fiziko-Khimicheskogo Obschchestva. Chast Fizicheskaya [Journal of the Russian Physicochemical Society. Physical Division]", language = "Russian", } @Article{Gamow:1926:TOP, author = "G. Gamow", title = "Sur la th{\'e}orie des ondes de phases de {M. L. de Broglie}. ({French}) [{On} the phase-wave theory of {M. L. de Broglie}]", journal = j-C-R-ACAD-SCI-PARIS, volume = "183", pages = "875--876", day = "13", month = nov, year = "1926", CODEN = "????", ISSN = "????", bibdate = "Wed May 30 09:07:56 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", abstract = "{Untersuchung {\"u}ber die {\"A}quivalenz von Wellengruppe und Korpuskel.}", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Comptes rendus de l'Acad{\'e}mie des sciences, Paris", language = "French", reviewer = "Dr. F. M{\"o}glich (Berlin)", xxjournal = "C. B.", } @Article{Gamow:1926:WMG, author = "George Gamow and Dmitri Iwanenko", title = "{Zur Wellentheorie der Materie}. ({German}) [{On} the wave theory of matter]", journal = j-Z-PHYSIK, volume = "39", number = "10--11", pages = "865--868", month = oct, year = "1926", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01451755", ISSN = "0044-3328", bibdate = "Tue May 29 07:35:34 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://www.springerlink.com/content/h8170266rv350j66/", ZMnumber = "JFM 52.0971.02", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", language = "German", remark = "In this paper, Gamow switched his name from the original romanization `Gamov' to the German spelling, which he retained throughout his life, except in French papers, and in transliterations of Russian papers, where it is `Gamov'.", } @Article{Gamow:1927:PFO, author = "G. A. Gamow", title = "The principle of fundamental observability in modern physics. ({Russian})", journal = j-USPEKHI-FIZ-NAUK, volume = "5", number = "??", pages = "386--??", month = "????", year = "1927", CODEN = "UFNAAG", ISSN = "0042-1294 (print), 1996-6652 (electronic)", ISSN-L = "0042-1294", bibdate = "Thu May 31 07:24:13 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", acknowledgement = ack-nhfb, ajournal = "Usp. Fiz. Nauk", author-dates = "George Gamow (1904--1968)", fjournal = "Uspekhi Fizicheskikh Nauk", journal-URL = "http://ufn.ru/en/articles/", language = "Russian", } @Article{Prokofiew:1927:ADL, author = "W. Prokofiew and George Gamow", title = "{Anomale Dispersion an den Linien der Hauptserie des Kaliums (Verh{\"a}ltnis der Dispersionskonstanten des roten und violetten Dubletts)}. ({German}) [{Anomalous} dispersion of the lines of the principal series of potassium (the ratio of the dispersion constants of the red and violet doublets)]", journal = j-Z-PHYSIK, volume = "44", number = "11--12", pages = "887--892", month = nov, year = "1927", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01390861", ISSN = "0044-3328", bibdate = "Tue May 29 07:31:09 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://www.springerlink.com/content/r1932n721m2mv828/", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", language = "German", } @Article{Gamow:1928:QAG, author = "George Gamow", title = "{Zur Quantentheorie des Atomkernes}. ({German}) [{On} the quantum theory of the atomic nucleus]", journal = j-Z-PHYSIK, volume = "51", number = "3--4", pages = "204--212", month = mar, year = "1928", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01343196", ISSN = "0044-3328", bibdate = "Tue May 29 07:41:17 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", note = "Reprinted in \cite[pages 77--85]{Beyer:1949:FNP}.", URL = "http://www.springerlink.com/content/mw52h8867mr4x185/", abstract = "JFM 54.0969.04", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", keywords = "Geiger--Nuttall relationship between radioisotope decay constants and energy of emitted alpha particles", language = "German", received = "2 August 1928", remark = "According to \cite[page 33]{Stuewer:1997:GAD}, this paper is the first to recognize that alpha particle decay is a quantum-mechanical tunneling problem, and its predictions better matched experiment than did those of an earlier model proposed by Ernest Rutherford. Alpher \cite{Alpher:1973:LNC} cites this paper, and two others \cite{Gurney:1928:WMR,Gamow:1929:QRK}, as the origin of the theory of alpha radioactivity. However, according to \cite[page 79]{Rigden:1995:JRO}, tunneling was predicted four months earlier by Robert Oppenheimer \cite{Oppenheimer:1928:QTA} in a paper received 28 March 1928.", } @Article{Gamow:1928:QTN, author = "George Gamow", title = "The Quantum Theory of Nuclear Disintegration", journal = j-NATURE, volume = "122", number = "3082", pages = "805--806", day = "24", month = nov, year = "1928", CODEN = "NATUAS", DOI = "https://doi.org/10.1038/122805b0", ISSN = "0028-0836 (print), 1476-4687 (electronic)", ISSN-L = "0028-0836", bibdate = "Mon May 28 17:58:11 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Nature", journal-URL = "http://www.nature.com/nature/archive/", } @Article{Gamow:1928:WCL, author = "Georgii Antonovich Gamow and Dmitrii Dmitrievich Ivanenko and Lev Davidovich Landau", title = "World Constants and Limiting Transition ({Russian})", journal = j-ZH-RUSS-FIZ-KHIM-OBSHCH-CHAST-FIZ, volume = "60", number = "1A", pages = "13--17", month = "????", year = "1928", CODEN = "ZRFFAS", ISSN = "0372-9834", bibdate = "Mon May 28 23:00:17 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", note = "Reprinted in \cite{Gamow:2002:UCB,Gamow:2002:WCL}. See comments about this little-known paper in \cite{Okun:2002:KPF}. The journal table of contents gives the German translation of the title as \booktitle{{\"U}ber die Weltkonstanten und den Grenz{\"u}bergang}.", acknowledgement = ack-nhfb, ajournal = "Zh. Russ. Fiz.-Khim. Obshch., Ch. Fiz.", author-dates = "George Gamow (1904--1968); Dmitrii Ivanenko (1904--1994); Lev Landau (1908--1968)", fjournal = "Zhurnal Russkogo Fiziko-Khimicheskogo Obshchestva, Chast Fizicheskaya", language = "Russian", remark = "This paper examines the transition from quantum mechanics to classical mechanics obtained by letting four physical constants --- Planck's constant ($h$ ), the reciprocal of the speed of light in vacuum ($ 1 / c $ ), and the gravitational constant ($G$ ) --- tend to zero. Gamow later used those letters in the name of his book character, Mr. C. G. H. Tompkins; see \cite[page 773]{Frenkel:1994:GGW} and \cite[page 95]{Stuewer:2018:AIN}. The paper is absent from Landau's two-volume \booktitle{Collection of Works} (Nauka, Moscow, 1969), and Okun reports that none of the authors ever cited this paper.", submitted = "20 October 1927", xxnote = "Some sources, including entry Okun:2002:KPF, says this is 1928; others have 1926. Okun gives a submission date of 20 October 1927, so it cannot be 1926.", xxpages = "477--??", xxtitle = "Universal constants and boundary crossings ({Russian})", } @Article{Gamow:1929:BQR, author = "George Gamow", title = "{Bemerkung zur Quantentheorie des radioaktiven Zerfalls}. ({German}) [{Note} on the quantum theory of radioactive decay]", journal = j-Z-PHYSIK, volume = "53", number = "7--8", pages = "601--604", month = jul, year = "1929", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01368139", ISSN = "0044-3328", bibdate = "Tue May 29 07:42:42 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://www.springerlink.com/content/w211101j12115804/", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", language = "German", } @Article{Gamow:1929:DSA, author = "George Gamow", title = "Discussion on the Structure of Atomic Nuclei", journal = j-PROC-R-SOC-LOND-SER-A-MATH-PHYS, volume = "123", number = "792", pages = "386--387", day = "6", month = apr, year = "1929", ISSN = "0950-1207 (print), 2053-9150 (electronic)", bibdate = "Wed May 30 16:19:07 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://www.jstor.org/stable/pdfplus/95202.pdf", acknowledgement = ack-nhfb, ajournal = "Proc. Roy. Soc. A", author-dates = "George Gamow (1904--1968)", fjournal = "Proceedings of the Royal Society of London. Series A, Containing Papers of a Mathematical and Physical Character", journal-URL = "http://rspa.royalsocietypublishing.org/", keywords = "liquid-drop model", remark = "According to \cite[page 36]{Stuewer:1997:GAD}, this paper is the first appearance in print of the liquid-drop model of nuclear structure. Gamow had discussed it with Niels Bohr in Copenhagen before traveling to visit Ernest Rutherford (then President of the Royal Society) in Cambridge. Bohr's first published paper (1909) was on the surface tension of water, so Gamow's ideas were likely familiar to Bohr, and Bohr recommended Gamow to Rutherford.", } @Article{Gamow:1929:QAG, author = "George Gamow", title = "{Zur Quantentheorie der Atomzertr{\"u}mmerung}. ({German}) [{On} the quantum theory of atomic fission]", journal = j-Z-PHYSIK, volume = "52", number = "7--8", pages = "510--515", month = jul, year = "1929", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01339451", ISSN = "0044-3328", bibdate = "Tue May 29 07:38:58 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://www.springerlink.com/content/t240444152t66876/", ZMnumber = "JFM 54.0969.05", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "{Zeitschrift f{\"u}r Physik}", journal-URL = "http://link.springer.com/journal/218", language = "German", remark = "Frenkel \cite{Frenkel:1994:CBG} reports that, on 29 January 1929, Gamow wrote from Cambridge to Paul Ehrenfest in Leiden, suggesting the use of high-powered $ \alpha $-particle beams to try to split atoms. Inspired by Gamow's suggestion (see \cite[page 106]{Stuewer:2018:AIN}), in 1932, John Cockcroft and Ernest Walton did just that in Rutherford's laboratory in Cambridge, work for which they received the 1951 Nobel Prize in Physics ``for their pioneer work on the transmutation of atomic nuclei by artificially accelerated atomic particles''.", } @Article{Gamow:1929:QRK, author = "George Gamow and Fritz G. Houtermans", title = "{Zur Quantenmechanik des radioaktiven Kerns}. ({German}) [{On} the quantum mechanics of the radioactive nucleus]", journal = j-Z-PHYSIK, volume = "52", number = "7--8", pages = "496--509", month = jul, year = "1929", CODEN = "ZEPYAA", DOI = "https://doi.org/10.1007/BF01339450", ISSN = "0044-3328", bibdate = "Tue May 29 07:37:27 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.b