ttps://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1939Natur.144..620G", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Nature", journal-URL = "http://www.nature.com/nature/archive/", } @Article{Gamow:1939:OGN, author = "George Gamow and Edward Teller", title = "On the Origin of {Great Nebul{\ae}}", journal = j-PHYS-REV, volume = "55", number = "7", pages = "654--657", day = "1", month = apr, year = "1939", CODEN = "PHRVAO", DOI = "https://doi.org/10.1103/PhysRev.55.654", ISSN = "0031-899X (print), 1536-6065 (electronic)", ISSN-L = "0031-899X", bibdate = "Thu Oct 6 19:08:03 MDT 2011", bibsource = "http://publish.aps.org/search; https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; https://www.math.utah.edu/pub/bibnet/authors/t/teller-edward.bib", URL = "http://adsabs.harvard.edu/abs/1939PhRv...55..654G; http://prola.aps.org/abstract/PR/v55/i7/p654_1", abstract = "The formation of condensation due to gravitational instability is discussed in a uniformly expanding space. It is shown that such condensations cannot be formed at the present stage of the development of the universe but could have been formed in the past when all linear dimensions were 600 times smaller. This corresponds to the stage at which, according to astronomical observations, nebulae have been separated from each other. To get the correct dimensions of nebulae it is necessary to accept that the velocities of particles at the moment of separation were about 140 km/sec. which strongly suggests that these particles were stars and not atoms. The type of expansion necessary for the formation of nebulae indicates that space is infinite and unlimitedly expanding.", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968); Edward Teller (1908--2003)", fjournal = "Physical Review", journal-URL = "http://prola.aps.org/browse/PR", LSnumber = "36", } @Article{Gamow:1939:PPS, author = "George Gamow", title = "Physical Possibilities of Stellar Evolution", journal = j-PHYS-REV, volume = "55", number = "8", pages = "718--725", month = apr, year = "1939", CODEN = "PHRVAO", DOI = "https://doi.org/10.1103/PhysRev.55.718", ISSN = "0031-899X (print), 1536-6065 (electronic)", ISSN-L = "0031-899X", bibdate = "Mon May 28 17:16:26 2012", bibsource = "http://publish.aps.org/search; https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1939PhRv...55..718G; http://link.aps.org/doi/10.1103/PhysRev.55.718", abstract = "The evolution of gaseous bodies, caused by different physical processes happening in their interior and serving as energy sources, is considered qualitatively and partially quantitatively in view of possible applications for the explanation of various observed states of known stars. It is shown that the part of evolution during which the main source of energy is given by thermonuclear reactions leads to a steadily increasing luminosity and goes over continuously into the contractive stage where the energy liberation is purely gravitational. The later stages of contraction and the transition into the state of degenerate gas model are discussed, in application to the present state of white dwarfs. Some remarks are made about the possibility of neutron-core formation in heavier stars, in application to the explosion phenomena observed in supernovae. An attempt is made to explain the energy production in red giants as due to thermonuclear reactions of light elements (lithium, beryllium, and boron), and the pulsation phenomena observed for Cepheid variables is interpreted as due to instability during the transitions from the giant branch into the main sequence.", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Physical Review", journal-URL = "http://prola.aps.org/browse/PR", } @Article{Teller:1939:FWC, author = "Edward Teller and George Gamow and J. A. Fleming", title = "{The Fifth Washington Conference on Theoretical Physics}", journal = j-SCIENCE, volume = "89", number = "2304", pages = "180--182", day = "24", month = feb, year = "1939", CODEN = "SCIEAS", DOI = "https://doi.org/10.1126/science.89.2304.180", ISSN = "0036-8075 (print), 1095-9203 (electronic)", ISSN-L = "0036-8075", bibdate = "Fri Oct 07 12:34:45 2011", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; https://www.math.utah.edu/pub/bibnet/authors/t/teller-edward.bib", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Science", journal-URL = "http://www.sciencemag.org/archive/", xxauthor = "C. F. Squire and F. G. Brickwedde and E. Teller and M. A. Tuve", } @Article{Anonymous:1940:BRB, author = "Anonymous", title = "Book Reviews: {{\booktitle{The Birth and Death of the Sun}}, by G. Gamow}", journal = "Popular Astronomy", volume = "48", pages = "397--??", month = dec, year = "1940", CODEN = "????", ISSN = "????", bibdate = "Tue May 29 21:18:22 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1940PA.....48..397G", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", } @Book{Gamow:1940:BDS, author = "George Gamow", title = "The Birth and Death of the Sun: Stellar Evolution and Subatomic Energy", publisher = pub-VIKING, address = pub-VIKING:adr, pages = "xiv + 1 + 238", year = "1940", LCCN = "QB44 .G26", bibdate = "Mon May 28 15:19:22 MDT 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; z3950.loc.gov:7090/Voyager", URL = "http://adsabs.harvard.edu/abs/1940QB44.G26.......", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", remark = "Charts on lining-papers. First published in June 1940.", subject = "Sun; Evolution; Stars; Atoms", } @Article{Gamow:1940:BPN, author = "George Gamow", title = "Basic Principles of the New Mechanics", journal = j-SCI-MONTHLY, volume = "51", number = "4", pages = "358--364", month = oct, year = "1940", CODEN = "SCMOAA", ISSN = "0096-3771 (print), 2327-7513 (electronic)", bibdate = "Tue May 29 07:53:43 MDT 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; JSTOR database", URL = "http://www.jstor.org/stable/pdfplus/17355.pdf", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "The Scientific Monthly", journal-URL = "http://www.jstor.org/journal/sciemont", } @Article{Gamow:1940:DMP, author = "George Gamow", title = "Dualism in the Modern Physics", journal = j-SCI-MONTHLY, volume = "50", number = "1", pages = "78--79", month = jan, year = "1940", CODEN = "SCMOAA", ISSN = "0096-3771 (print), 2327-7513 (electronic)", bibdate = "Tue May 29 07:53:43 MDT 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; JSTOR database", URL = "http://www.jstor.org/stable/pdfplus/17022.pdf", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "The Scientific Monthly", journal-URL = "http://www.jstor.org/journal/sciemont", } @Article{Gamow:1940:ES, author = "George Gamow", title = "The Evolution of the Stars", journal = j-NATURE, volume = "146", number = "3690", pages = "97--97", day = "20", month = jul, year = "1940", CODEN = "NATUAS", DOI = "https://doi.org/10.1038/146097a0", ISSN = "0028-0836 (print), 1476-4687 (electronic)", ISSN-L = "0028-0836", bibdate = "Mon May 28 17:58:11 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1940Natur.146...97G", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Nature", journal-URL = "http://www.nature.com/nature/archive/", } @Article{Gamow:1940:PRN, author = "George Gamow and M. Schoenberg", title = "The Possible Role of Neutrinos in Stellar Evolution", journal = j-PHYS-REV, volume = "58", number = "12", pages = "1117--1117", day = "15", month = dec, year = "1940", CODEN = "PHRVAO", DOI = "https://doi.org/10.1103/PhysRev.58.1117", ISSN = "0031-899X (print), 1536-6065 (electronic)", ISSN-L = "0031-899X", bibdate = "Mon May 28 17:27:28 MDT 2012", bibsource = "http://publish.aps.org/search; https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1940PhRv...58.1117G; http://prola.aps.org/abstract/PR/v58/i12/p1117_1", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Physical Review", journal-URL = "http://prola.aps.org/browse/PR", } @Article{Gamow:1940:RUS, author = "George Gamow", title = "Review: Has the {Universe} a Soul? : {{\booktitle{The Soul of the Universe}} by Gustaf Stromberg}", journal = j-SCI-MONTHLY, volume = "51", number = "6", pages = "564--565", month = dec, year = "1940", CODEN = "SCMOAA", ISSN = "0096-3771 (print), 2327-7513 (electronic)", bibdate = "Tue May 29 07:53:43 MDT 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; JSTOR database", URL = "http://adsabs.harvard.edu/abs/1940SciMo..51..564S; http://www.jstor.org/stable/pdfplus/17151.pdf", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "The Scientific Monthly", journal-URL = "http://www.jstor.org/journal/sciemont", } @Book{Gamow:1941:BEP, author = "George Gamow", title = "Biography of the {Earth}: Its Past, Present, and Future", publisher = pub-VIKING, address = pub-VIKING:adr, pages = "xiii, [1], 242", year = "1941", LCCN = "QE501 .G35", bibdate = "Mon May 28 15:19:57 MDT 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib; z3950.loc.gov:7090/Voyager", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", subject = "Earth; Cosmogony", } @Article{Gamow:1941:HSB, author = "George Gamow", title = "How Stars are Born", journal = "American Weekly", volume = "??", number = "??", pages = "??--??", day = "22", month = jun, year = "1941", CODEN = "????", ISSN = "????", bibdate = "Thu May 31 12:21:41 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", } @Article{Gamow:1941:NTS, author = "George Gamow and M. Schoenberg", title = "Neutrino Theory of Stellar Collapse", journal = j-PHYS-REV, volume = "59", number = "7", pages = "539--547", day = "1", month = apr, year = "1941", CODEN = "PHRVAO", DOI = "https://doi.org/10.1103/PhysRev.59.539", ISSN = "0031-899X (print), 1536-6065 (electronic)", ISSN-L = "0031-899X", bibdate = "Mon May 28 17:27:28 MDT 2012", bibsource = "http://publish.aps.org/search; https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1941PhRv...59..539G; http://prola.aps.org/abstract/PR/v59/i7/p539_1", ZMnumber = "Zbl 0026.04803", abstract = "At the very high temperatures and densities which must exist in the interior of contracting stars during the later stages of their evolution, one must expect a special type of nuclear processes accompanied by the emission of a large number of neutrinos. These neutrinos penetrating almost without difficulty the body of the star, must carry away very large amounts of energy and prevent the central temperature from rising above a certain limit. This must cause a rapid contraction of the stellar body ultimately resulting in a catastrophic collapse. It is shown that energy losses through the neutrinos produced in reactions between free electrons and oxygen nuclei can cause a complete collapse of the star within the time period of half an hour. Although the main energy losses in such collapses are due to neutrino emission which escapes direct observation. the heating of the body of a collapsing star must necessarily lead to the rapid expansion of the outer layers and the tremendous increase of luminosity. It is suggested that stellar collapses of this kind are responsible for the phenomena of novae and supernovae, the difference between the two being probably due to the difference of their masses.", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Physical Review", journal-URL = "http://prola.aps.org/browse/PR", } @Article{Gamow:1941:NVS, author = "George Gamow", title = "Neutrinos vs. Supernov{\ae}", journal = j-SCI-MONTHLY, volume = "54", number = "??", pages = "??--??", month = "????", year = "1941 (??)", CODEN = "SCMOAA", ISSN = "0096-3771 (print), 2327-7513 (electronic)", bibdate = "Thu May 31 11:52:05 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "The Scientific Monthly", journal-URL = "http://www.jstor.org/journal/sciemont", } @Article{Gamow:1941:OSB, author = "G. Gamow", title = "Our Sun is Bound to Explode", journal = "Popular Astronomy", volume = "49", pages = "360--??", month = aug, year = "1941", CODEN = "????", ISSN = "????", bibdate = "Tue May 29 21:18:22 2012", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://adsabs.harvard.edu/abs/1941PA.....49..360G", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", } @Article{Gamow:1941:RID, author = "George Gamow", title = "Relative Importance of Different Elements for Neutrino Production", journal = j-PHYS-REV, volume = "59", number = "7", pages = "617--618", day = "1", month = apr, year = "1941", CODEN = "PHRVAO", DOI = "https://doi.org/10.1103/PhysRev.59.617", ISSN = "0031-899X (print), 1536-6065 (electronic)", ISSN-L = "0031-899X", bibdate = "Mon May 28 17:27:28 MDT 2012", bibsource = "http://publish.aps.org/search; https://www.math.utah.edu/pub/bibnet/authors/g/gamow-george.bib", URL = "http://prola.aps.org/abstract/PR/v59/i7/p617_1", ZMnumber = "Zbl 0026.04804", acknowledgement = ack-nhfb, author-dates = "George Gamow (1904--1968)", fjournal = "Physical Review", journal-URL = "http://prola.aps.org/browse/PR", } @