@Article{Meitner:1906:ASG, author = "Lise Meitner", title = "{{\"U}ber die Absorption der $ \alpha $- und $ \beta $-Strahlen}. ({German}) [{On} the absorption of $ \alpha $- and $ \beta $-rays]", journal = j-PHYSIKAL-Z, volume = "7", number = "17", pages = "588--590", day = "1", month = sep, year = "1906", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:17:26 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/nyp.33433062733229?urlappend=;seq=164", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "Physikalische Zeitschrift", journal-URL = "http://catalog.hathitrust.org/Record/000541302", language = "German", } @Article{Meitner:1906:EFSa, author = "Lise Meitner", title = "{{\"U}ber einige Folgerungen, die sich aus den {\it Fresnel}schen Reflexionsformeln ergeben}. ({German}) [{On} some of the implications of the {{\it Fresnel\/}} reflection formulas]", journal = j-S-B-KAISERL-AKAD-WISS, volume = "115", number = "6", pages = "859--869", year = "1906", CODEN = "SWWPAX", ISSN = "0376-2629", bibdate = "Sat May 19 17:57:35 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://books.google.com/books?id=BFQhAQAAMAAJ&pg=859", ZMnumber = "37.0869.02", acknowledgement = ack-nhfb, ajournal = "{Sitb. der mathem.-naturw. KL, Abt. IIa}", author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "{Akademie der Wissenschaften in Wien, Mathematisch-Naturwissenschaftliche Klasse, Sitzungsberichte, Abteilung IIa}", journal-URL = "http://catalog.hathitrust.org/Record/003086676", language = "German", remark = "The cited journal name, \booktitle{Wiener Berichte}, is wrong. I also found it cited with the journal abbreviations \booktitle{S.-A. Wien. Ber.}, and as \booktitle{Sitz. Ber. der Wiener Akad., IIa Abt.}. I finally found the volume in the Google Books archive: it has the title \booktitle{Sitzungsberichte der Kaiserlichen Akademie der Wissenschaften: Mathematisch-Naturwissenschaftliche Klasse}, hundertf{\"u}nfszehnter Band (Wien, 1906). The issue cover on page 777 adds at the bottom Enh{\"a}lt die Abhandlungen aus dem Gebiete der Mathematik, Astronomie, Physik, Meteorologie und der Mechanik. The paper front matter says Vorgelegt in der Sitzung am 12. Juli 1906, and the running page footer on the previous page says \booktitle{Sitb. der mathem.-naturw. KL; CXV. Bd., Abt. IIa}. The CAS Source Index (CASSI) Search Tool names this journal as \booktitle{Sitzungsberichte der Akademie der Wissenschaften in Wien, Mathematisch-Naturwissenschaftliche Klasse, Abteilung 2A: Mathematik, Astronomie, Physik, Meteorologie und Technik} (CODEN SWWPAX, CODEN 0376-2629).", xxjournal = "Wiener Berichte", } @Article{Meitner:1906:EFSb, author = "Lise Meitner", title = "{{\"U}ber einige Folgerungen, die sich aus den {\it Fresnel}schen Reflexionsformeln ergeben}. ({German}) [{On} some of the implications of the {{\it Fresnel\/}} reflection formulas]", journal = "S. Ber. preuss Akad. Wiss.; Math. Natur-Klasse, Abt. IIa", volume = "115", number = "??", pages = "259--286", month = jun, year = "1906", bibdate = "Wed May 23 10:16:36 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", remark = "I think the journal abbreviation here may be wrong; there were several journal name changes, and the one that includes the year 1906 is the journal Sitzungsberichte der K{\"o}niglichen Preussischen Akademie der Wissenschaften (abbreviation: Sitzungsber. K. Preuss. Akad. Wiss.), published during the years 1882--1921, according to the CAS Source Index (CASSI) Search Tool. There is no article with this title in the journal cited in entry Meitner:1906:EFSa.", } @Article{Meitner:1906:WIK, author = "Lise Meitner", title = "{Warmeleitung in inhomogenen Korpern. II}. ({German}) [{Heat} conduction in inhomogeneous bodies. {II}]", journal = "Phys. Inst. Wien, IIa", volume = "115", number = "??", pages = "125--137", month = feb, year = "1906", bibdate = "Wed May 23 10:10:16 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", note = "Dr. phil. dissertation.", author-dates = "Lise Meitner (7 November 1878--27 October 1968)", language = "German", } @Article{Meitner:1907:ZSG, author = "Lise Meitner", title = "{{\"U}ber die Zerstreuung der $ \alpha $-Strahlen}. ({German}) [{On} the dispersion of $ \alpha $ rays]", journal = j-PHYSIKAL-Z, volume = "8", number = "15", pages = "489--491", day = "1", month = aug, year = "1907", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:33:30 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/nyp.33433062733237?urlappend=;seq=73", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "Physikalische Zeitschrift", journal-URL = "http://catalog.hathitrust.org/Record/000541302", language = "German", } @Article{Hahn:1908:ACN, author = "Otto Hahn and Lise Meitner", title = "{Actinium C, ein neues kurzlebiges Produkt des Actiniums}. ({German}) [{Actinium C}, a new short-lived product of actinium]", journal = j-PHYSIKAL-Z, volume = "9", number = "20", pages = "649--655", day = "15", month = oct, year = "1908", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:42:39 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/mdp.39015068320947?urlappend=;seq=677", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "Physikalische Zeitschrift", journal-URL = "http://catalog.hathitrust.org/Record/000541302", language = "German", received = "28 August 1908", } @Article{Hahn:1908:ASE, author = "Otto Hahn and Lise Meitner", title = "{{\"U}ber die Absorption der $ \beta $-Strahlen einiger Radioelemente}. ({German}) [{On} the absorption of $ \beta $ rays of some radioelements]", journal = j-PHYSIKAL-Z, volume = "9", number = "10", pages = "321--333", day = "15", month = may, year = "1908", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:34:36 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/mdp.39015068320947?urlappend=;seq=347", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "Physikalische Zeitschrift", journal-URL = "http://catalog.hathitrust.org/Record/000541302", language = "German", } @Article{Hahn:1908:SAG, author = "Otto Hahn and Lise Meitner", title = "{{\"U}ber die $ \beta $-Strahlen des Actiniums}. ({German}) [{On} the $ \beta $ rays of actinium]", journal = j-PHYSIKAL-Z, volume = "9", number = "21", pages = "697--702", day = "25", month = oct, year = "1908", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:44:28 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/mdp.39015068320947?urlappend=;seq=725", acknowledgement = ack-nhfb, author-dates = "Lise Meitner (7 November 1878--27 October 1968)", fjournal = "Physikalische Zeitschrift", journal-URL = "http://catalog.hathitrust.org/Record/000541302", language = "German", } @Article{Hahn:1909:ASG, author = "Otto Hahn and Lise Meitner", title = "{{\"U}ber das Absorptionsgesetz der $ \beta $-Strahlen}. ({German}) [{On} the absorption law of $ \beta $ rays]", journal = j-PHYSIKAL-Z, volume = "10", number = "24", pages = "948--950", day = "1", month = dec, year = "1909", CODEN = "PHZTAO", ISSN = "0369-982X", bibdate = "Wed May 23 10:50:56 2018", bibsource = "https://www.math.utah.edu/pub/bibnet/authors/m/meitner-lise.bib", URL = "https://hdl.handle.net/2027/nyp.33433090815253?urlappend=;seq=1017", acknowledge